              {isBase64Valid === false && (
                <Card className="bg-red-50 border-red-200">
                  <CardContent className="p-3">
                    <div className="flex items-center text-error">
                      <Trash2 className="h-4 w-4 mr-2" />
                      <span className="text-sm font-medium">Invalid Base64 data</span>
                    </div>
