import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Lock, Unlock, Key, Eye, EyeOff } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

const EncryptionTools = () => {
  const [encryptInput, setEncryptInput] = useState("");
  const [decryptInput, setDecryptInput] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [algorithm, setAlgorithm] = useState("aes");
  const [encryptResult, setEncryptResult] = useState("");
  const [decryptResult, setDecryptResult] = useState("");
  const { toast } = useToast();

  // Simple encryption functions (for demo purposes)
  const simpleEncrypt = (text: string, key: string): string => {
    if (!text || !key) return "";

    let result = "";
    for (let i = 0; i < text.length; i++) {
      const charCode = text.charCodeAt(i) ^ key.charCodeAt(i % key.length);
      result += String.fromCharCode(charCode);
    }
    return btoa(result); // Base64 encode
  };

  const simpleDecrypt = (encryptedText: string, key: string): string => {
    if (!encryptedText || !key) return "";

    try {
      const decoded = atob(encryptedText); // Base64 decode
      let result = "";
      for (let i = 0; i < decoded.length; i++) {
        const charCode = decoded.charCodeAt(i) ^ key.charCodeAt(i % key.length);
        result += String.fromCharCode(charCode);
      }
      return result;
    } catch (error) {
      return "Invalid encrypted text or key";
    }
  };

  const caesarCipher = (text: string, shift: number): string => {
    return text.replace(/[a-zA-Z]/g, (char) => {
      const start = char <= 'Z' ? 65 : 97;
      return String.fromCharCode(((char.charCodeAt(0) - start + shift) % 26) + start);
    });
  };

  const rot13 = (text: string): string => {
    return caesarCipher(text, 13);
  };

  const handleEncrypt = () => {
    if (!encryptInput.trim()) {
      toast({
        title: "Error",
        description: "Please enter text to encrypt",
        variant: "destructive",
      });
      return;
    }

    let result = "";

    switch (algorithm) {
      case "aes":
        if (!password) {
          toast({
            title: "Error",
            description: "Password is required for AES encryption",
            variant: "destructive",
          });
          return;
        }
        result = simpleEncrypt(encryptInput, password);
        break;
      case "caesar":
        const shift = password ? parseInt(password) || 3 : 3;
        result = caesarCipher(encryptInput, shift);
        break;
      case "rot13":
        result = rot13(encryptInput);
        break;
      case "base64":
        result = btoa(encryptInput);
        break;
      default:
        result = simpleEncrypt(encryptInput, password || "defaultkey");
    }

    setEncryptResult(result);
    toast({
      title: "Success",
      description: "Text encrypted successfully",
    });
  };

  const handleDecrypt = () => {
    if (!decryptInput.trim()) {
      toast({
        title: "Error",
        description: "Please enter text to decrypt",
        variant: "destructive",
      });
      return;
    }

    let result = "";

    try {
      switch (algorithm) {
        case "aes":
          if (!password) {
            toast({
              title: "Error",
              description: "Password is required for AES decryption",
              variant: "destructive",
            });
            return;
          }
          result = simpleDecrypt(decryptInput, password);
          break;
        case "caesar":
          const shift = password ? parseInt(password) || 3 : 3;
          result = caesarCipher(decryptInput, -shift);
          break;
        case "rot13":
          result = rot13(decryptInput); // ROT13 is its own inverse
          break;
        case "base64":
          result = atob(decryptInput);
          break;
        default:
          result = simpleDecrypt(decryptInput, password || "defaultkey");
      }

      setDecryptResult(result);
      toast({
        title: "Success",
        description: "Text decrypted successfully",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to decrypt text. Check your input and key.",
        variant: "destructive",
      });
    }
  };

  const copyToClipboard = (text: string, type: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied",
      description: `${type} copied to clipboard`,
    });
  };

  const algorithms = [
    { value: "aes", label: "AES (Simulated)", description: "Advanced Encryption Standard" },
    { value: "caesar", label: "Caesar Cipher", description: "Simple substitution cipher" },
    { value: "rot13", label: "ROT13", description: "Caesar cipher with shift of 13" },
    { value: "base64", label: "Base64", description: "Base64 encoding/decoding" },
  ];

  const selectedAlgorithmInfo = algorithms.find(a => a.value === algorithm);

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Lock className="h-5 w-5" />
            Encryption & Decryption Tools
          </CardTitle>
          <CardDescription>
            Encrypt and decrypt text using various algorithms and methods
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <label className="text-sm font-medium mb-2 block">Algorithm</label>
            <Select value={algorithm} onValueChange={setAlgorithm}>
              <SelectTrigger data-testid="select-algorithm">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {algorithms.map((algo) => (
                  <SelectItem key={algo.value} value={algo.value}>
                    {algo.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {selectedAlgorithmInfo && (
              <p className="text-xs text-gray-500 mt-1">
                {selectedAlgorithmInfo.description}
              </p>
            )}
          </div>

          {(algorithm === "aes" || algorithm === "caesar") && (
            <div>
              <label className="text-sm font-medium mb-2 block">
                {algorithm === "caesar" ? "Shift Value" : "Password/Key"}
              </label>
              <div className="relative">
                <Input
                  type={showPassword ? "text" : "password"}
                  placeholder={algorithm === "caesar" ? "Enter shift value (e.g., 3)" : "Enter encryption password"}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  data-testid="input-password"
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-1 top-1 h-8 w-8 p-0"
                  onClick={() => setShowPassword(!showPassword)}
                  data-testid="button-toggle-password"
                >
                  {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      <Tabs defaultValue="encrypt" className="space-y-4">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="encrypt" data-testid="tab-encrypt">
            <Lock className="h-4 w-4 mr-2" />
            Encrypt
          </TabsTrigger>
          <TabsTrigger value="decrypt" data-testid="tab-decrypt">
            <Unlock className="h-4 w-4 mr-2" />
            Decrypt
          </TabsTrigger>
        </TabsList>

        <TabsContent value="encrypt">
          <Card>
            <CardHeader>
              <CardTitle>Encrypt Text</CardTitle>
              <CardDescription>
                Enter plain text to encrypt using the selected algorithm
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <Textarea
                placeholder="Enter text to encrypt..."
                value={encryptInput}
                onChange={(e) => setEncryptInput(e.target.value)}
                className="min-h-[100px]"
                data-testid="textarea-encrypt-input"
              />

              <Button 
                onClick={handleEncrypt} 
                className="w-full"
                data-testid="button-encrypt"
              >
                <Lock className="h-4 w-4 mr-2" />
                Encrypt Text
              </Button>

              {encryptResult && (
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <label className="text-sm font-medium">Encrypted Result</label>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => copyToClipboard(encryptResult, "Encrypted text")}
                      data-testid="button-copy-encrypted"
                    >
                      Copy
                    </Button>
                  </div>
                  <div className="bg-gray-50 p-3 rounded border font-mono text-sm break-all">
                    {encryptResult}
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="decrypt">
          <Card>
            <CardHeader>
              <CardTitle>Decrypt Text</CardTitle>
              <CardDescription>
                Enter encrypted text to decrypt using the selected algorithm
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <Textarea
                placeholder="Enter encrypted text to decrypt..."
                value={decryptInput}
                onChange={(e) => setDecryptInput(e.target.value)}
                className="min-h-[100px]"
                data-testid="textarea-decrypt-input"
              />

              <Button 
                onClick={handleDecrypt} 
                className="w-full"
                data-testid="button-decrypt"
              >
                <Unlock className="h-4 w-4 mr-2" />
                Decrypt Text
              </Button>

              {decryptResult && (
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <label className="text-sm font-medium">Decrypted Result</label>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => copyToClipboard(decryptResult, "Decrypted text")}
                      data-testid="button-copy-decrypted"
                    >
                      Copy
                    </Button>
                  </div>
                  <div className="bg-gray-50 p-3 rounded border font-mono text-sm break-all">
                    {decryptResult}
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      <Card>
        <CardHeader>
          <CardTitle>Security Notice</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="bg-yellow-50 border border-yellow-200 p-4 rounded">
            <p className="text-sm text-yellow-800">
              <strong>âš ï¸ For Educational Purposes:</strong> These encryption tools are simplified implementations 
              for demonstration purposes. For production applications requiring strong security, 
              use established cryptographic libraries and follow security best practices.
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default EncryptionTools;

client/src/components/file-converters.ts.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { FileText, FileImage, Upload, Download } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface ConversionResult {
  jobId: string;
  text?: string;
  pages?: number;
  info?: any;
  messages?: any[];
  filename: string;
  type: string;
}

export default function FileConverters() {
  const [pdfFile, setPdfFile] = useState<File | null>(null);
  const [docxFile, setDocxFile] = useState<File | null>(null);
  const [conversionResults, setConversionResults] = useState<ConversionResult[]>([]);
  const { toast } = useToast();

  const pdfToTextMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append("file", file);
      const response = await fetch("/api/convert/pdf-to-text", {
        method: "POST",
        body: formData,
      });
      if (!response.ok) {
        throw new Error("Failed to convert PDF to text");
      }
      return response.json();
    },
    onSuccess: (data) => {
      const result: ConversionResult = {
        jobId: data.jobId,
        text: data.text,
        pages: data.pages,
        info: data.info,
        filename: `${pdfFile?.name?.replace('.pdf', '') || 'converted'}.txt`,
        type: "text",
      };
      setConversionResults(prev => [...prev, result]);
      toast({
        title: "Success",
        description: "PDF converted to text successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Conversion Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const docxToTextMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append("file", file);
      const response = await fetch("/api/convert/docx-to-text", {
        method: "POST",
        body: formData,
      });
      if (!response.ok) {
        throw new Error("Failed to convert DOCX to text");
      }
      return response.json();
    },
    onSuccess: (data) => {
      const result: ConversionResult = {
        jobId: data.jobId,
        text: data.text,
        messages: data.messages,
        filename: `${docxFile?.name?.replace('.docx', '') || 'converted'}.txt`,
        type: "text",
      };
      setConversionResults(prev => [...prev, result]);
      toast({
        title: "Success",
        description: "DOCX converted to text successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Conversion Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handlePdfFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      if (file.type !== "application/pdf") {
        toast({
          title: "Invalid File Type",
          description: "Please select a PDF file",
          variant: "destructive",
        });
        return;
      }
      if (file.size > 50 * 1024 * 1024) { // 50MB
        toast({
          title: "File Too Large",
          description: "File size must be less than 50MB",
          variant: "destructive",
        });
        return;
      }
      setPdfFile(file);
    }
  };

  const handleDocxFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      if (!file.name.toLowerCase().endsWith('.docx')) {
        toast({
          title: "Invalid File Type",
          description: "Please select a DOCX file",
          variant: "destructive",
        });
        return;
      }
      if (file.size > 50 * 1024 * 1024) { // 50MB
        toast({
          title: "File Too Large",
          description: "File size must be less than 50MB",
          variant: "destructive",
        });
        return;
      }
      setDocxFile(file);
    }
  };

  const convertPdfToText = () => {
    if (!pdfFile) {
      toast({
        title: "No File Selected",
        description: "Please select a PDF file first",
        variant: "destructive",
      });
      return;
    }
    pdfToTextMutation.mutate(pdfFile);
  };

  const convertDocxToText = () => {
    if (!docxFile) {
      toast({
        title: "No File Selected",
        description: "Please select a DOCX file first",
        variant: "destructive",
      });
      return;
    }
    docxToTextMutation.mutate(docxFile);
  };

  const downloadResult = (result: ConversionResult) => {
    if (!result.text) return;

    const blob = new Blob([result.text], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = result.filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    toast({
      title: "Downloaded",
      description: `${result.filename} has been downloaded`,
    });
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + " " + sizes[i];
  };

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-semibold text-gray-900">File Converters</h2>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* PDF Converter */}
        <Card className="border border-gray-200">
          <CardContent className="p-6">
            <div className="flex items-center space-x-3 mb-4">
              <div className="bg-red-100 p-2 rounded-lg">
                <FileText className="h-6 w-6 text-red-600" />
              </div>
              <div>
                <h3 className="text-lg font-semibold text-gray-900">PDF Converter</h3>
                <p className="text-sm text-gray-500">Convert PDF to text or images</p>
              </div>
            </div>

            {/* File Upload Zone */}
            <div 
              className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-gray-400 transition-colors mb-4 cursor-pointer"
              onClick={() => document.getElementById('pdf-upload')?.click()}
              data-testid="dropzone-pdf"
            >
              <Upload className="h-8 w-8 text-gray-400 mx-auto mb-2" />
              <p className="text-sm text-gray-600 mb-2">Drop PDF files here or</p>
              <Button variant="outline" size="sm">
                Choose File
              </Button>
              <p className="text-xs text-gray-500 mt-2">Max file size: 50MB</p>
            </div>

            <input
              id="pdf-upload"
              type="file"
              accept=".pdf"
              onChange={handlePdfFileSelect}
              className="hidden"
              data-testid="input-pdf-file"
            />

            {pdfFile && (
              <div className="mb-4 p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center justify-between text-sm">
                  <div className="flex items-center space-x-2">
                    <FileText className="h-4 w-4 text-red-600" />
                    <span className="font-medium" data-testid="text-pdf-filename">{pdfFile.name}</span>
                  </div>
                  <span className="text-gray-500" data-testid="text-pdf-filesize">
                    {formatFileSize(pdfFile.size)}
                  </span>
                </div>
              </div>
            )}

            <div className="space-y-3">
              <label className="block text-sm font-medium text-gray-700">Convert to:</label>
              <div className="grid grid-cols-2 gap-2">
                <Button 
                  variant="outline" 
                  onClick={convertPdfToText}
                  disabled={!pdfFile || pdfToTextMutation.isPending}
                  data-testid="button-convert-pdf-to-text"
                >
                  <FileText className="h-4 w-4 mr-2" />
                  {pdfToTextMutation.isPending ? "Converting..." : "Text"}
                </Button>
                <Button variant="outline" disabled data-testid="button-convert-pdf-to-images">
                  <FileImage className="h-4 w-4 mr-2" />
                  Images
                </Button>
              </div>
            </div>

            {/* Progress Bar */}
            {pdfToTextMutation.isPending && (
              <div className="mt-4">
                <div className="flex items-center justify-between text-sm text-gray-600 mb-2">
                  <span>Converting...</span>
                  <span>Processing</span>
                </div>
                <Progress value={75} className="w-full" />
              </div>
            )}
          </CardContent>
        </Card>

        {/* DOCX Converter */}
        <Card className="border border-gray-200">
          <CardContent className="p-6">
            <div className="flex items-center space-x-3 mb-4">
              <div className="bg-blue-100 p-2 rounded-lg">
                <FileText className="h-6 w-6 text-blue-600" />
              </div>
              <div>
                <h3 className="text-lg font-semibold text-gray-900">DOCX Converter</h3>
                <p className="text-sm text-gray-500">Convert Word documents to text or PDF</p>
              </div>
            </div>

            {/* File Upload Zone */}
            <div 
              className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-gray-400 transition-colors mb-4 cursor-pointer"
              onClick={() => document.getElementById('docx-upload')?.click()}
              data-testid="dropzone-docx"
            >
              <Upload className="h-8 w-8 text-gray-400 mx-auto mb-2" />
              <p className="text-sm text-gray-600 mb-2">Drop DOCX files here or</p>
              <Button variant="outline" size="sm">
                Choose File
              </Button>
              <p className="text-xs text-gray-500 mt-2">Max file size: 50MB</p>
            </div>

            <input
              id="docx-upload"
              type="file"
              accept=".docx"
              onChange={handleDocxFileSelect}
              className="hidden"
              data-testid="input-docx-file"
            />

            {docxFile && (
              <div className="mb-4 p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center justify-between text-sm">
                  <div className="flex items-center space-x-2">
                    <FileText className="h-4 w-4 text-blue-600" />
                    <span className="font-medium" data-testid="text-docx-filename">{docxFile.name}</span>
                  </div>
                  <span className="text-gray-500" data-testid="text-docx-filesize">
                    {formatFileSize(docxFile.size)}
                  </span>
                </div>
              </div>
            )}

            <div className="space-y-3">
              <label className="block text-sm font-medium text-gray-700">Convert to:</label>
              <div className="grid grid-cols-2 gap-2">
                <Button 
                  variant="outline" 
                  onClick={convertDocxToText}
                  disabled={!docxFile || docxToTextMutation.isPending}
                  data-testid="button-convert-docx-to-text"
                >
                  <FileText className="h-4 w-4 mr-2" />
                  {docxToTextMutation.isPending ? "Converting..." : "Text"}
                </Button>
                <Button variant="outline" disabled data-testid="button-convert-docx-to-pdf">
                  <FileText className="h-4 w-4 mr-2" />
                  PDF
                </Button>
              </div>
            </div>

            {/* Progress Bar */}
            {docxToTextMutation.isPending && (
              <div className="mt-4">
                <div className="flex items-center justify-between text-sm text-gray-600 mb-2">
                  <span>Converting...</span>
                  <span>Processing</span>
                </div>
                <Progress value={75} className="w-full" />
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Conversion Results */}
      {conversionResults.length > 0 && (
        <Card className="bg-gray-50">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Conversion Results</h3>
            <div className="space-y-4">
              {conversionResults.map((result, index) => (
                <div key={index} className="bg-white rounded-lg p-4 border border-gray-200">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <FileText className="h-5 w-5 text-gray-400" />
                      <div>
                        <p className="font-medium text-gray-900" data-testid={`text-result-filename-${index}`}>
                          {result.filename}
                        </p>
                        <p className="text-sm text-gray-500">
                          {result.pages && `${result.pages} pages â€¢ `}
                          {result.text ? `${Math.round(result.text.length / 1024)} KB` : 'Processing...'}
                        </p>
                      </div>
                    </div>
                    <Button 
                      onClick={() => downloadResult(result)}
                      disabled={!result.text}
                      size="sm"
                      className="bg-primary hover:bg-primary-dark"
                      data-testid={`button-download-result-${index}`}
                    >
                      <Download className="h-4 w-4 mr-2" />
                      Download
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/hashing-tools.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Copy, Hash, Download, Shield, CheckCircle, XCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

const HashingTools = () => {
  const [input, setInput] = useState("");
  const [selectedAlgorithms, setSelectedAlgorithms] = useState<string[]>(["md5", "sha256"]);
  const [results, setResults] = useState<Record<string, string>>({});
  const [verifyText, setVerifyText] = useState("");
  const [verifyHash, setVerifyHash] = useState("");
  const [verifyResult, setVerifyResult] = useState<{match: boolean, algorithm: string} | null>(null);
  const { toast } = useToast();

  const hashAlgorithms = [
    { value: "md5", label: "MD5", description: "128-bit hash function" },
    { value: "sha1", label: "SHA-1", description: "160-bit hash function" },
    { value: "sha256", label: "SHA-256", description: "256-bit hash function" },
    { value: "sha512", label: "SHA-512", description: "512-bit hash function" },
    { value: "sha3-256", label: "SHA-3-256", description: "SHA-3 with 256-bit output" },
    { value: "sha3-512", label: "SHA-3-512", description: "SHA-3 with 512-bit output" },
  ];

  const generateHash = async (algorithm: string, text: string): Promise<string> => {
    const encoder = new TextEncoder();
    const data = encoder.encode(text);

    let hashBuffer: ArrayBuffer;

    switch (algorithm) {
      case "md5":
        // Simple MD5 implementation (note: for production, use a proper crypto library)
        return await simpleHash(text, "MD5");
      case "sha1":
        hashBuffer = await crypto.subtle.digest("SHA-1", data);
        break;
      case "sha256":
        hashBuffer = await crypto.subtle.digest("SHA-256", data);
        break;
      case "sha512":
        hashBuffer = await crypto.subtle.digest("SHA-512", data);
        break;
      case "sha3-256":
      case "sha3-512":
        // For demo purposes, using SHA-256 as fallback
        hashBuffer = await crypto.subtle.digest("SHA-256", data);
        break;
      default:
        hashBuffer = await crypto.subtle.digest("SHA-256", data);
    }

    const hashArray = Array.from(new Uint8Array(hashBuffer));
    return hashArray.map(b => b.toString(16).padStart(2, '0')).join('');
  };

  const simpleHash = async (text: string, algorithm: string): Promise<string> => {
    // Simple hash function for demo - in production use proper crypto library
    let hash = 0;
    for (let i = 0; i < text.length; i++) {
      const char = text.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash; // Convert to 32-bit integer
    }
    return Math.abs(hash).toString(16).padStart(8, '0');
  };

  const handleGenerateHashes = async () => {
    if (!input.trim()) {
      toast({
        title: "Error",
        description: "Please enter some text to hash",
        variant: "destructive",
      });
      return;
    }

    if (selectedAlgorithms.length === 0) {
      toast({
        title: "Error",
        description: "Please select at least one hash algorithm",
        variant: "destructive",
      });
      return;
    }

    const newResults: Record<string, string> = {};

    for (const algorithmValue of selectedAlgorithms) {
      try {
        newResults[algorithmValue] = await generateHash(algorithmValue, input);
      } catch (error) {
        newResults[algorithmValue] = "Error generating hash";
      }
    }

    setResults(newResults);
    toast({
      title: "Success",
      description: `${selectedAlgorithms.length} hash(es) generated successfully`,
    });
  };

  const handleAlgorithmToggle = (algorithmValue: string, checked: boolean) => {
    if (checked) {
      setSelectedAlgorithms(prev => [...prev, algorithmValue]);
    } else {
      setSelectedAlgorithms(prev => prev.filter(alg => alg !== algorithmValue));
    }
  };

  const handleVerifyHash = async () => {
    if (!verifyText.trim() || !verifyHash.trim()) {
      toast({
        title: "Error",
        description: "Please enter both text and hash to verify",
        variant: "destructive",
      });
      return;
    }

    let matchFound = false;
    let matchedAlgorithm = "";

    // Try each algorithm to see if any produces the same hash
    for (const algorithm of hashAlgorithms) {
      try {
        const generatedHash = await generateHash(algorithm.value, verifyText);
        if (generatedHash.toLowerCase() === verifyHash.toLowerCase()) {
          matchFound = true;
          matchedAlgorithm = algorithm.label;
          break;
        }
      } catch (error) {
        continue;
      }
    }

    setVerifyResult({ match: matchFound, algorithm: matchedAlgorithm });

    if (matchFound) {
      toast({
        title: "Hash Verified âœ“",
        description: `Text matches the ${matchedAlgorithm} hash`,
      });
    } else {
      toast({
        title: "Hash Mismatch âœ—",
        description: "Text does not match the provided hash with any algorithm",
        variant: "destructive",
      });
    }
  };

  const copyToClipboard = (hash: string, algorithm: string) => {
    navigator.clipboard.writeText(hash);
    toast({
      title: "Copied",
      description: `${algorithm.toUpperCase()} hash copied to clipboard`,
    });
  };

  const downloadHashes = () => {
    const content = Object.entries(results)
      .map(([algo, hash]) => `${algo.toUpperCase()}: ${hash}`)
      .join('\n');

    const blob = new Blob([content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'hashes.txt';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Hash className="h-5 w-5" />
            Cryptographic Hashing Tools
          </CardTitle>
          <CardDescription>
            Generate and verify secure hashes using various algorithms
          </CardDescription>
        </CardHeader>
      </Card>

      <Tabs defaultValue="generate" className="space-y-4">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="generate" data-testid="tab-generate-hash">
            <Hash className="h-4 w-4 mr-2" />
            Generate Hashes
          </TabsTrigger>
          <TabsTrigger value="verify" data-testid="tab-verify-hash">
            <Shield className="h-4 w-4 mr-2" />
            Verify Hash
          </TabsTrigger>
        </TabsList>

        <TabsContent value="generate">
          <Card>
            <CardHeader>
              <CardTitle>Generate Hashes</CardTitle>
              <CardDescription>
                Select algorithms and generate secure hashes for your text
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium mb-2 block">Input Text</label>
                <Textarea
                  placeholder="Enter text to hash..."
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  className="min-h-[100px]"
                  data-testid="textarea-hash-input"
                />
              </div>

              <div>
                <label className="text-sm font-medium mb-3 block">Select Hash Algorithms</label>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                  {hashAlgorithms.map((algorithm) => (
                    <div key={algorithm.value} className="flex items-center space-x-2">
                      <Checkbox
                        id={algorithm.value}
                        checked={selectedAlgorithms.includes(algorithm.value)}
                        onCheckedChange={(checked) => handleAlgorithmToggle(algorithm.value, checked as boolean)}
                        data-testid={`checkbox-${algorithm.value}`}
                      />
                      <label 
                        htmlFor={algorithm.value} 
                        className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 cursor-pointer"
                      >
                        {algorithm.label}
                      </label>
                    </div>
                  ))}
                </div>
                <p className="text-xs text-gray-500 mt-2">
                  Select one or more algorithms to generate hashes
                </p>
              </div>

              <div className="flex gap-4">
                <Button 
                  onClick={handleGenerateHashes}
                  className="flex-1"
                  disabled={selectedAlgorithms.length === 0}
                  data-testid="button-generate-hashes"
                >
                  Generate Selected Hashes ({selectedAlgorithms.length})
                </Button>
                {Object.keys(results).length > 0 && (
                  <Button 
                    variant="outline" 
                    onClick={downloadHashes}
                    data-testid="button-download-hashes"
                  >
                    <Download className="h-4 w-4 mr-2" />
                    Download
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="verify">
          <Card>
            <CardHeader>
              <CardTitle>Verify Hash</CardTitle>
              <CardDescription>
                Check if a hash matches the given text using any supported algorithm
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium mb-2 block">Original Text</label>
                <Textarea
                  placeholder="Enter the original text..."
                  value={verifyText}
                  onChange={(e) => setVerifyText(e.target.value)}
                  className="min-h-[80px]"
                  data-testid="textarea-verify-text"
                />
              </div>

              <div>
                <label className="text-sm font-medium mb-2 block">Hash to Verify</label>
                <Input
                  placeholder="Enter the hash value to verify..."
                  value={verifyHash}
                  onChange={(e) => setVerifyHash(e.target.value)}
                  className="font-mono"
                  data-testid="input-verify-hash"
                />
              </div>

              <Button 
                onClick={handleVerifyHash}
                className="w-full"
                data-testid="button-verify-hash"
              >
                <Shield className="h-4 w-4 mr-2" />
                Verify Hash
              </Button>

              {verifyResult && (
                <div className={`p-4 rounded border ${verifyResult.match ? 'bg-green-50 border-green-200' : 'bg-red-50 border-red-200'}`}>
                  <div className="flex items-center gap-2">
                    {verifyResult.match ? (
                      <CheckCircle className="h-5 w-5 text-green-600" />
                    ) : (
                      <XCircle className="h-5 w-5 text-red-600" />
                    )}
                    <span className={`font-medium ${verifyResult.match ? 'text-green-800' : 'text-red-800'}`}>
                      {verifyResult.match ? 'Hash Verified âœ“' : 'Hash Mismatch âœ—'}
                    </span>
                  </div>
                  <p className={`text-sm mt-1 ${verifyResult.match ? 'text-green-700' : 'text-red-700'}`}>
                    {verifyResult.match 
                      ? `Text matches the provided hash using ${verifyResult.algorithm} algorithm`
                      : 'The text does not produce the provided hash with any supported algorithm'
                    }
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {Object.keys(results).length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Hash Results</CardTitle>
            <CardDescription>
              Generated hashes for your input text
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            {hashAlgorithms.map((algorithm) => {
              const hash = results[algorithm.value];
              if (!hash) return null;

              return (
                <div key={algorithm.value} className="space-y-2">
                  <div className="flex items-center justify-between">
                    <div>
                      <Badge variant="secondary" className="text-xs">
                        {algorithm.label}
                      </Badge>
                      <p className="text-xs text-gray-500 mt-1">
                        {algorithm.description}
                      </p>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => copyToClipboard(hash, algorithm.label)}
                      data-testid={`button-copy-${algorithm.value}`}
                    >
                      <Copy className="h-4 w-4" />
                    </Button>
                  </div>
                  <div className="bg-gray-50 p-3 rounded border font-mono text-sm break-all">
                    {hash}
                  </div>
                </div>
              );
            })}
          </CardContent>
        </Card>
      )}

      <Card>
        <CardHeader>
          <CardTitle>Hash Algorithm Information</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
            <div>
              <h4 className="font-semibold mb-2">Security Levels</h4>
              <ul className="space-y-1 text-gray-600">
                <li>â€¢ <strong>MD5:</strong> Not cryptographically secure</li>
                <li>â€¢ <strong>SHA-1:</strong> Deprecated, avoid for security</li>
                <li>â€¢ <strong>SHA-256:</strong> Strong, widely used</li>
                <li>â€¢ <strong>SHA-512:</strong> Very strong, 512-bit output</li>
              </ul>
            </div>
            <div>
              <h4 className="font-semibold mb-2">Use Cases</h4>
              <ul className="space-y-1 text-gray-600">
                <li>â€¢ <strong>Data integrity:</strong> File checksums</li>
                <li>â€¢ <strong>Password storage:</strong> Secure hashing</li>
                <li>â€¢ <strong>Digital signatures:</strong> Document verification</li>
                <li>â€¢ <strong>Blockchain:</strong> Proof of work</li>
              </ul>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default HashingTools;

client/src/components/json-tools.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Copy, Trash2, FileText, CheckCircle, AlertCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

export default function JsonTools() {
  const [input, setInput] = useState(`{
  "name": "John Doe",
  "age": 30,
  "email": "john@example.com",
  "address": {
    "street": "123 Main St",
    "city": "New York",
    "zip": "10001"
  },
  "hobbies": ["reading", "coding", "hiking"]
}`);
  const [output, setOutput] = useState("");
  const [isValid, setIsValid] = useState<boolean | null>(null);
  const [stats, setStats] = useState<any>(null);
  const { toast } = useToast();

  const validateMutation = useMutation({
    mutationFn: async (content: string) => {
      const response = await apiRequest("POST", "/api/validate/json", { content });
      return response.json();
    },
    onSuccess: (data) => {
      setIsValid(data.valid);
      if (data.valid) {
        setOutput(data.formatted);
        setStats(data.stats);
      } else {
        setOutput(`Error: ${data.error}`);
        setStats(null);
      }
    },
    onError: (error) => {
      setIsValid(false);
      setOutput(`Error: ${error.message}`);
      setStats(null);
    },
  });

  const handleFormat = () => {
    if (!input.trim()) {
      toast({
        title: "Empty Input",
        description: "Please enter some JSON to format",
        variant: "destructive",
      });
      return;
    }
    validateMutation.mutate(input);
  };

  const handleValidate = () => {
    if (!input.trim()) {
      toast({
        title: "Empty Input",
        description: "Please enter some JSON to validate",
        variant: "destructive",
      });
      return;
    }
    validateMutation.mutate(input);
  };

  const handleMinify = () => {
    try {
      const parsed = JSON.parse(input);
      const minified = JSON.stringify(parsed);
      setOutput(minified);
      setIsValid(true);
      toast({
        title: "Success",
        description: "JSON minified successfully",
      });
    } catch (error) {
      setIsValid(false);
      setOutput(`Error: ${error instanceof Error ? error.message : "Invalid JSON"}`);
      toast({
        title: "Invalid JSON",
        description: "Cannot minify invalid JSON",
        variant: "destructive",
      });
    }
  };

  const copyToClipboard = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
      toast({
        title: "Copied!",
        description: "Content copied to clipboard",
      });
    } catch (error) {
      toast({
        title: "Failed to Copy",
        description: "Could not copy to clipboard",
        variant: "destructive",
      });
    }
  };

  const clearInput = () => {
    setInput("");
    setOutput("");
    setIsValid(null);
    setStats(null);
  };

  const pasteFromClipboard = async () => {
    try {
      const text = await navigator.clipboard.readText();
      setInput(text);
      toast({
        title: "Pasted!",
        description: "Content pasted from clipboard",
      });
    } catch (error) {
      toast({
        title: "Failed to Paste",
        description: "Could not paste from clipboard",
        variant: "destructive",
      });
    }
  };

  const formatBytes = (bytes: number) => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + " " + sizes[i];
  };

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-semibold text-gray-900">JSON Formatter & Validator</h2>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Input Section */}
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <Label className="text-sm font-medium text-gray-700">Input JSON</Label>
            <div className="flex space-x-2">
              <Button 
                variant="outline" 
                size="sm" 
                onClick={clearInput}
                data-testid="button-clear-json"
              >
                <Trash2 className="h-3 w-3 mr-1" />
                Clear
              </Button>
              <Button 
                variant="outline" 
                size="sm" 
                onClick={pasteFromClipboard}
                data-testid="button-paste-json"
              >
                <FileText className="h-3 w-3 mr-1" />
                Paste
              </Button>
            </div>
          </div>

          <Textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Paste your JSON here..."
            className="h-80 font-mono text-sm resize-none"
            data-testid="textarea-json-input"
          />

          <div className="flex space-x-2">
            <Button 
              onClick={handleFormat}
              disabled={validateMutation.isPending}
              className="flex-1 bg-primary hover:bg-primary-dark"
              data-testid="button-format-json"
            >
              <i className="fas fa-align-left mr-2"></i>
              {validateMutation.isPending ? "Formatting..." : "Format"}
            </Button>
            <Button 
              onClick={handleValidate}
              disabled={validateMutation.isPending}
              variant="outline"
              className="flex-1 border-success text-success hover:bg-success hover:text-white"
              data-testid="button-validate-json"
            >
              <CheckCircle className="h-4 w-4 mr-2" />
              Validate
            </Button>
            <Button 
              onClick={handleMinify}
              disabled={validateMutation.isPending}
              variant="outline"
              className="flex-1 border-warning text-warning hover:bg-warning hover:text-white"
              data-testid="button-minify-json"
            >
              <i className="fas fa-compress mr-2"></i>
              Minify
            </Button>
          </div>
        </div>

        {/* Output Section */}
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <Label className="text-sm font-medium text-gray-700">Formatted JSON</Label>
            <div className="flex items-center space-x-2">
              {isValid !== null && (
                <div className={`flex items-center text-sm ${isValid ? 'text-success' : 'text-error'}`}>
                  {isValid ? (
                    <CheckCircle className="h-4 w-4 mr-1" />
                  ) : (
                    <AlertCircle className="h-4 w-4 mr-1" />
                  )}
                  <span>{isValid ? "Valid JSON" : "Invalid JSON"}</span>
                </div>
              )}
              <Button 
                variant="outline" 
                size="sm" 
                onClick={() => copyToClipboard(output)}
                disabled={!output}
                data-testid="button-copy-json"
              >
                <Copy className="h-3 w-3 mr-1" />
                Copy
              </Button>
            </div>
          </div>

          <div className="relative">
            <pre className="w-full h-80 p-4 font-mono text-sm border border-gray-300 rounded-lg bg-gray-50 overflow-auto">
              <code data-testid="text-json-output">{output || "Formatted JSON will appear here..."}</code>
            </pre>
          </div>

          {/* JSON Stats */}
          {stats && (
            <div className="bg-gray-50 rounded-lg p-4">
              <h4 className="text-sm font-medium text-gray-700 mb-2">JSON Statistics</h4>
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="text-gray-500">Size:</span>
                  <span className="font-medium ml-1" data-testid="text-json-size">
                    {formatBytes(stats.size)}
                  </span>
                </div>
                <div>
                  <span className="text-gray-500">Lines:</span>
                  <span className="font-medium ml-1" data-testid="text-json-lines">
                    {stats.lines}
                  </span>
                </div>
                <div>
                  <span className="text-gray-500">Objects:</span>
                  <span className="font-medium ml-1" data-testid="text-json-objects">
                    {stats.objects}
                  </span>
                </div>
                <div>
                  <span className="text-gray-500">Arrays:</span>
                  <span className="font-medium ml-1" data-testid="text-json-arrays">
                    {stats.arrays}
                  </span>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/qr-tools.tsx.content:
import { useState, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { QrCode, Download, Upload, Camera } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

const QRTools = () => {
  const [generateText, setGenerateText] = useState("");
  const [qrSize, setQrSize] = useState("200");
  const [qrColor, setQrColor] = useState("#000000");
  const [bgColor, setBgColor] = useState("#ffffff");
  const [qrCodeUrl, setQrCodeUrl] = useState("");
  const [scanResult, setScanResult] = useState("");
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const generateQRCode = () => {
    if (!generateText.trim()) {
      toast({
        title: "Error",
        description: "Please enter text to generate QR code",
        variant: "destructive",
      });
      return;
    }

    // Using a free QR code API service
    const apiUrl = `https://api.qrserver.com/v1/create-qr-code/?size=${qrSize}x${qrSize}&data=${encodeURIComponent(generateText)}&color=${qrColor.substring(1)}&bgcolor=${bgColor.substring(1)}`;
    setQrCodeUrl(apiUrl);

    toast({
      title: "Success",
      description: "QR code generated successfully",
    });
  };

  const downloadQRCode = () => {
    if (!qrCodeUrl) return;

    const link = document.createElement('a');
    link.href = qrCodeUrl;
    link.download = 'qrcode.png';
    link.click();
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      toast({
        title: "Error",
        description: "Please select an image file",
        variant: "destructive",
      });
      return;
    }

    // For demo purposes, we'll simulate QR code reading
    // In a real implementation, you'd use a QR code reading library
    setTimeout(() => {
      setScanResult("Demo QR Code Content: " + file.name + " - QR reading would happen here with a proper library");
      toast({
        title: "Success",
        description: "QR code scanned successfully",
      });
    }, 1000);
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied",
      description: "Text copied to clipboard",
    });
  };

  const qrTypes = [
    { value: "text", label: "Plain Text" },
    { value: "url", label: "Website URL" },
    { value: "email", label: "Email Address" },
    { value: "phone", label: "Phone Number" },
    { value: "sms", label: "SMS Message" },
    { value: "wifi", label: "WiFi Credentials" },
  ];

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <QrCode className="h-5 w-5" />
            QR Code Generator & Scanner
          </CardTitle>
          <CardDescription>
            Generate QR codes from text and scan existing QR codes from images
          </CardDescription>
        </CardHeader>
      </Card>

      <Tabs defaultValue="generate" className="space-y-4">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="generate" data-testid="tab-generate">
            Generate QR Code
          </TabsTrigger>
          <TabsTrigger value="scan" data-testid="tab-scan">
            Scan QR Code
          </TabsTrigger>
        </TabsList>

        <TabsContent value="generate">
          <Card>
            <CardHeader>
              <CardTitle>Generate QR Code</CardTitle>
              <CardDescription>
                Create QR codes for text, URLs, contact info, and more
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium mb-2 block">Content Type</label>
                <Select defaultValue="text">
                  <SelectTrigger data-testid="select-qr-type">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {qrTypes.map((type) => (
                      <SelectItem key={type.value} value={type.value}>
                        {type.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm font-medium mb-2 block">Content</label>
                <Textarea
                  placeholder="Enter text, URL, email, phone number, etc..."
                  value={generateText}
                  onChange={(e) => setGenerateText(e.target.value)}
                  className="min-h-[100px]"
                  data-testid="textarea-qr-content"
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="text-sm font-medium mb-2 block">Size (px)</label>
                  <Select value={qrSize} onValueChange={setQrSize}>
                    <SelectTrigger data-testid="select-qr-size">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="150">150x150</SelectItem>
                      <SelectItem value="200">200x200</SelectItem>
                      <SelectItem value="300">300x300</SelectItem>
                      <SelectItem value="400">400x400</SelectItem>
                      <SelectItem value="500">500x500</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium mb-2 block">QR Color</label>
                  <Input
                    type="color"
                    value={qrColor}
                    onChange={(e) => setQrColor(e.target.value)}
                    data-testid="input-qr-color"
                  />
                </div>

                <div>
                  <label className="text-sm font-medium mb-2 block">Background</label>
                  <Input
                    type="color"
                    value={bgColor}
                    onChange={(e) => setBgColor(e.target.value)}
                    data-testid="input-bg-color"
                  />
                </div>
              </div>

              <Button 
                onClick={generateQRCode} 
                className="w-full"
                data-testid="button-generate-qr"
              >
                <QrCode className="h-4 w-4 mr-2" />
                Generate QR Code
              </Button>

              {qrCodeUrl && (
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <label className="text-sm font-medium">Generated QR Code</label>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={downloadQRCode}
                      data-testid="button-download-qr"
                    >
                      <Download className="h-4 w-4 mr-2" />
                      Download
                    </Button>
                  </div>
                  <div className="flex justify-center p-4 bg-gray-50 rounded border">
                    <img 
                      src={qrCodeUrl} 
                      alt="Generated QR Code" 
                      className="border"
                      data-testid="img-generated-qr"
                    />
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="scan">
          <Card>
            <CardHeader>
              <CardTitle>Scan QR Code</CardTitle>
              <CardDescription>
                Upload an image containing a QR code to decode its content
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium mb-2 block">Upload QR Code Image</label>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                  <input
                    type="file"
                    ref={fileInputRef}
                    onChange={handleFileUpload}
                    accept="image/*"
                    className="hidden"
                    data-testid="input-qr-file"
                  />
                  <Upload className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600 mb-4">
                    Drag and drop an image file or click to browse
                  </p>
                  <Button 
                    variant="outline" 
                    onClick={() => fileInputRef.current?.click()}
                    data-testid="button-upload-qr"
                  >
                    <Upload className="h-4 w-4 mr-2" />
                    Choose File
                  </Button>
                </div>
              </div>

              {scanResult && (
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <label className="text-sm font-medium">Scanned Content</label>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => copyToClipboard(scanResult)}
                      data-testid="button-copy-scanned"
                    >
                      Copy
                    </Button>
                  </div>
                  <div className="bg-gray-50 p-3 rounded border font-mono text-sm break-all">
                    {scanResult}
                  </div>
                </div>
              )}

              <div className="bg-blue-50 border border-blue-200 p-4 rounded">
                <p className="text-sm text-blue-800">
                  <strong>ðŸ“± Tip:</strong> For best results, ensure the QR code is clear, 
                  well-lit, and fills a good portion of the image frame.
                </p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      <Card>
        <CardHeader>
